sources.timeline.scss:
.timeline {
  margin-bottom: 20px;
  padding: 0 15px;
  &-item {
    display: flex;
    border-bottom: 1px solid #888;
    padding-top: 20px;
    &-avatar {
      width: 49px;
      margin: 0 5px;
      .avatar-img {
        margin: auto;
        width: 47px;
        height: 47px;
        border-radius: 50%;
        background-image: url($url);
        background-size: cover;
        background-position: center;
        position: relative;
        &-hover {
          height: 100%;
          width: 100%;
          border-radius: 50%;
          position: absolute;
          transition: all .3s;
        };
      };
      .avatar-img:hover .avatar-img-hover {
        background-color: $bg-hover;
        cursor: pointer;
      }
    };
    &-content {
      flex-grow: 1;
      &-header {
        display: flex;
        margin-bottom: 5px;
        .post-author {
          text-decoration: none;
          &-name {
            font-weight: 600;
            color: #000;
          };
          &-tick {

          };
          &-trend {
            color: #888;
          };  
        };
        .post-time {
          color: #888;
          text-decoration: none;
          margin-left: 15px;
          flex-grow: 1;
        };  
        .dropdown-btn {

        };
      };
      &-main {
        padding-bottom: 10px;
        margin-bottom: 10px;
        .description {
          width: 490px;
          margin-bottom: 5px;
        };
        .sub-description {
          height: 285px;
          width: 100%;
          background-image: url($url);
          background-size: cover;
          background-position: center;
          border-radius: $border-image;
        };
      };
      &-footer {
        display: flex;
        margin-right: 60px;
        margin-bottom: 10px;
        justify-content: space-between;
        .social {
          &-icon {
            position: relative;
            &-hover {
              @include box-social;
            };
          };
          &-view {
            margin-left: 5px;
          };
        };
        .social:hover .social-icon-hover {
          background-color: $bg-hover;
        };
      };
    };
  };
  &-item:hover {
    background-color: rgb(245, 248, 250);
  };
};

sources.right-siderbar.scss:
.right-siderbar {
  font-family: $font;
  width: 442px;
  &-content {
    margin-left: 30px;
    margin-right: 55px;
    &-header {
      margin-top: 10px;
      margin-bottom: 50px;
      @import './search-form.scss';
    };
    &-trend {
      border-radius: $border-image;
      background-color: rgba(170, 184, 194, 0.2);
      margin-bottom: 10px;
      @import './trend.scss';
    };
    &-follow {
      border-radius: $border-image;
      background-color: rgba(170, 184, 194, 0.2);
      margin-bottom: 25px;
      @import './follow.scss';
    }
    &-footer {
      font-size: 14px;
      padding-left: 15px;
      .line-1 {
        margin-bottom: 10px;
        &-item {
          &-text {
            color: #555;
            margin-right: 10px;
            text-decoration: none;
          };
          &-text:hover {
            text-decoration: underline;
          }
        };
      };
      .line-2 {
        margin-bottom: 10px;
        &-item {
          &-text {
            color: #555;
            margin-right: 10px;
            text-decoration: none;
          };
          &-text:hover {
            text-decoration: underline;
          };
          &-icon {

          };
        };
      };
      .inc {
        color: #555;
      };
    };
  };
}

sources.search-form.scss:
.search-form {
  position: relative;
  margin-right: 40px;
  &-input {
    font-size: 16px;
    padding: 10px 0;
    padding-left: 40px;
    width: 100%;
    background-color: #eee;
    border-radius: 30px;
    outline: none;
    border: none;
    transition: all .3s;
  };
  &-input:focus {
    background-color: #fff;
    border: 1px solid $primary-color;
  }
  &-icon {
    position: absolute;
    top: 10px;
    left: 15px;
  };
}

sources.trend.scss:
.trend-header {
  padding: 15px 15px;
  margin-bottom: 1px;
  display: flex;
  border-bottom: 1px solid #ddd;
  &-title {
    font-size: 19px;
    font-weight: 600;
    flex-grow: 1;
  };
  &-setting {
    color: $primary-color;
    position: relative;
    height: 16px;
    &-hover {
      height: 37px;
      width: 37px;
      border-radius: 50%;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      transition: all .3s;
    };
    &-hover:hover {
      background-color: $bg-hover;
      cursor: pointer;
    }
  };
}

.trend-item {
  padding: 10px 15px;
  border-bottom: 1px solid #ddd;
  transition: all .3s;
  &-text {
    color: #555;
    font-size: 15px;
    margin-bottom: 5px;
  };
  &-hashtag {
    font-weight: 600;
    margin-bottom: 5px;
  };
  &-view {
    color: #555;
  };
}

.trend-item:hover,
.trend-more:hover {
  background-color: $trend-hover;
  cursor: pointer;
}

.trend-more {
  padding: 15px;
  border-bottom-right-radius: 14px;
  border-bottom-left-radius: 14px;
  transition: all .3s;
  &-text {
    color: $primary-color;
    text-decoration: none;
  }
}

sources.follow.scss:
.follow-header {
  padding: 15px 15px;
  margin-bottom: 1px;
  display: flex;
  border-bottom: 1px solid #ddd;
  &-title {
    font-size: 19px;
    font-weight: 600;
  };
}

.follow-item {
  padding: 10px 15px;
  border-bottom: 1px solid #ddd;
  display: flex;
  align-items: center;
  transition: all .3s;
  &-avatar {
    width: 39px;
    height: 39px;
    margin-right: 5px;
    border-radius: 50%;
    background-image: url($url);
    background-size: cover;
    background-position: center;
    &-hover {
      width: 100%;
      height: 100%;
      border-radius: 50%;
      transition: all .3s;
    };
    &-hover:hover {
      background-color: $bg-hover;
      cursor: pointer;
    }
  };
  &-info {
    text-decoration: none;
    margin-left: 5px;
    flex-grow: 1;
    &-name {
      color: #000;
      font-weight: 600;
      margin-bottom: 3px;
    };
    &-mail {
      color: #555;
    };
  };
  &-btn {
    border: 1px solid $primary-color;
    padding: 5px 15px;
    border-radius: 30px;
    &-text {
      font-size: 15px;
      color: $primary-color;
      font-weight: 600;
    };
  };
  &-btn:hover {
    background-color: $bg-hover;
  }
}

.follow-item:hover,
.follow-more:hover {
  background-color: $trend-hover;
  cursor: pointer;
}

.follow-more {
  padding: 15px;
  border-bottom-right-radius: 14px;
  border-bottom-left-radius: 14px;
  transition: all .3s;
  &-text {
    color: $primary-color;
    text-decoration: none;
  }
}
